	$(document.body).on('click', ".generate-struktur", function(e){
		e.preventDefault();
		var scat = $(this).data("id");
		$("#struktur").empty().append(generate_struktur(scat));
		$(this).attr('class','generate-struktur fontblack70');
		$('.generate-struktur').each(function(){
			if($(this).data("id") != scat) $(this).attr('class','generate-struktur fontblue');
		});

		$("a.tooltipcontent").each(function() {
			//e.preventDefault();
			var elem = $(this);
			var elid = elem.attr("id");
			var im = elem.data("tooltip-image");
			var c = elem.data("tooltip-content");
			var t = (im !== '') ? '<img class="img-tooltip" src="'+im+'"/><br/>'+c : c;
			if(t !== ''){
				$("#"+elid).aToolTip({
					tipContent: t,
					toolTipClass: "col-md-12 tooltip-content"
				});
			}
		});
	});

